import React, {Component} from 'react';
import HomeStyles from "./HomePage.module.css"
import images from "./ImagesForCarousel";

class HomePage extends Component {
    render() {
        return (
            <React.Fragment>
                <ImageCarousel rotationTime={5000}/>
                <ContactDivider/>
            </React.Fragment>
        );
    }
}

class ImageCarousel extends Component {
    constructor(props) {
        super(props);
        this.state = {
            images: images,
            totalImages: images.length,
            currentImageIndex: 0,
            slideImageIndex: 0,
            activeSlideCSS: HomeStyles.slide,
            shouldUpdate: true,
            rotationTime: this.props.rotationTime || 5000,
            timer: null
        }
    }

    componentDidMount() {
        this.setState({timer: setInterval(this.nextImage, this.state.rotationTime)});
    }

    componentWillUnmount() {
        clearInterval(this.state.timer);
    }

    autoplay = () => {
        clearInterval(this.state.timer);
        this.setState({timer: setInterval(this.nextImage, this.state.rotationTime)});
    }

    nextImage = () => {
        this.setState({shouldUpdate: true, slideImageIndex: this.state.currentImageIndex});
        this.state.currentImageIndex < this.state.totalImages - 1
            ? this.setState({currentImageIndex: this.state.currentImageIndex + 1})
            : this.setState({currentImageIndex: 0});
        this.setState({activeSlideCSS: HomeStyles.slideLeft});
        this.autoplay();
    }

    previousImage = () => {
        this.setState({shouldUpdate: true})
        this.state.currentImageIndex > 0
            ? this.setState({slideImageIndex: this.state.currentImageIndex - 1})
            : this.setState({slideImageIndex: this.state.totalImages - 1});
        this.setState({activeSlideCSS: HomeStyles.slideRight});
        this.autoplay();
    }

    render() {
        let slideImage = this.state.images[this.state.slideImageIndex];
        let currentImage = this.state.images[this.state.currentImageIndex];

        return (
            <React.Fragment>
                <button onClick={this.previousImage}>Previous</button>
                <button onClick={this.nextImage}>Next</button>
                <div className={HomeStyles.carouselContainer}>
                    {this.state.shouldUpdate ? <img key={Math.random()} className={this.state.activeSlideCSS}
                                                    onAnimationEndCapture={this.state.activeSlideCSS === HomeStyles.slideRight ? () => this.setState({
                                                        shouldUpdate: false,
                                                        currentImageIndex: this.state.slideImageIndex
                                                    }) : null}
                                                    src={slideImage.image}
                                                    alt={slideImage.alt} height="725px" width="100%"/> : null}
                    <img src={currentImage.image} alt={currentImage.alt} height="725px" width="100%"/>
                </div>
            </React.Fragment>
        );
    }
}

const ContactDivider = () =>
    <div className={HomeStyles.headerContainer}>
        <div className={HomeStyles.header}>
            > Please contact us for detailed information for each service
        </div>
    </div>


export default HomePage;
