import './App.css';

import {useLocation} from 'react-router-dom'

import Header from "../Components/Header/Header";
import TopNavigationBar from "../Components/Navigation_Bar/NavigationBar";
import Footer from "../Components/Footer/Footer";
import React from "react";

function App() {
    return (
        <React.Fragment>
            <Header/>
            <TopNavigationBar/>
            {useLocation().pathname !== "../login" ? <Footer/> : null}
        </React.Fragment>
    );
}

export default App;
