import React, {Component} from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    NavLink
} from 'react-router-dom';

import * as NavigationLinks from "./NavigationLinks";
import NavigationBarStyle from "./NavigationBar.css";
import Logo from "./images/logo_sharp.png";

class TopNavigationBar extends Component {
    setCurrentLinkColor (id) {
        document.getElementById(id).style.color = "#9155A7";
    }

    render() {
        return (
            <Router>
                <div>
                    <div className={NavigationBarStyle.navigationContainer}>
                    <img src={Logo} alt="Logo" height="200px" width="400px"/>
                    <nav>
                        <ul className={NavigationBarStyle.linksContainer}>
                            {NavigationLinks.mainPageLinks.map(link => (<NavItem linkObject={link} textColor={"#605E5E"} clickedTextColor={"#9155A7"}/>))}
                        </ul>
                    </nav>
                    </div>
                    <Switch>
                        {NavigationLinks.allLinks.map(link => (
                            <Route exact path={link.endpoint} component={link.page}/>))}
                    </Switch>
                </div>
            </Router>
        );
    }
}

class NavItem extends Component {
    changeTextColor = () => {
        this.setState({textColor:this.props.clickedTextColor})
    }

    constructor(props) {
        super(props);
        this.state = {
            linkObject: this.props.linkObject,
            textColor: this.props.textColor,
            clickedTextColor: this.props.clickedTextColor
        }
    }

    render() {
       return (
             <li className={NavigationBarStyle.links} id={this.state.linkObject.name}>
                 <NavLink exact activeClassName={NavigationBarStyle.activeLink} to={this.state.linkObject.endpoint}>{this.state.linkObject.name}</NavLink>
             </li>
       );
    }
}

export default TopNavigationBar;
