import React, {Component, useState} from 'react';
import {Link} from "react-router-dom";
import {mainPageLinks, signUpPageLink} from "../../Components/Navigation_Bar/NavigationLinks";
import LoginStyles from "./LoginPage.module.css"

class LoginPage extends Component {
    render() {
        return (
            <Login/>
        );
    }
}

const Login = () => {
    const [showLoginForm, setShowLoginForm] = useState(false);

    return (
        <div className={LoginStyles.pageContainer}>
            <Link to={mainPageLinks[0].endpoint}>
                <button className={LoginStyles.closeLoginButton}>X</button>
            </Link>
                <div className={LoginStyles.loginContainer}>
                    <h1>Login</h1>
                    <h4>Don't have an account? </h4>
                    <Link to={signUpPageLink.endpoint}>
                        <button>Sign up</button>
                    </Link>
                    {showLoginForm ? <LoginForm/> :
                        <button onClick={() => setShowLoginForm(true)}>Login with email</button>}
                </div>
            </div>
    );
}


const LoginForm = () => {
    return (
        <div>
            <form>
                <label>Email</label>
                <input type="text"/>
                <label>Password</label>
                <input type="text"/>
            </form>
            <button>Forgot password?</button>
            <button>Log In</button>
        </div>
    );
}

export default LoginPage;
