import React, {Component} from 'react';
import HomeStyles from "./HomePage.module.css"
import images from "./ImagesForCarousel";

class HomePage extends Component {
    render() {
        return (
            <React.Fragment>
                <ImageCarousel rotationTime={5000}/>
                <ContactDivider/>
            </React.Fragment>
        );
    }
}

class ImageCarousel extends Component {
    constructor(props) {
        super(props);
        this.state = {
            images: images,
            totalImages: images.length,
            currentImageIndex: 1,
            previousImageIndex: 0,
            activeSlideCSS: HomeStyles.slide,
            rotationTime: this.props.rotationTime || 5000,
            timer: null
        }
        this.shouldSlide = true;
    }

    componentDidMount() {
        this.setState({timer: setInterval(this.nextImage, this.state.rotationTime)});
    }

    componentWillUnmount() {
        clearInterval(this.state.timer);
    }

    autoplay = () => {
        clearInterval(this.state.timer);
        this.setState({timer: setInterval(this.nextImage, this.state.rotationTime)});
        this.changeComplete = true;
    }

    nextImage = () => {
        this.shouldSlide = false;
        console.log("Next 1 Current: " + this.state.currentImageIndex)
        console.log("Next 1 Previous: " + this.state.previousImageIndex)
        this.setState({previousImageIndex: this.state.currentImageIndex});
        this.state.currentImageIndex < this.state.totalImages - 1
            ? this.setState({currentImageIndex: this.state.currentImageIndex + 1})
            : this.setState({currentImageIndex: 0});
        this.setState({activeSlideCSS: HomeStyles.slideLeft});
        console.log("Next 2 Current: " + this.state.currentImageIndex)
        console.log("Next 2 Previous: " + this.state.previousImageIndex)
        this.shouldSlide = true;
        this.autoplay();
    }

    previousImage = () => {
        this.shouldSlide = false;
        this.setState({previousImageIndex: this.state.currentImageIndex});
        this.state.currentImageIndex > 0
            ? this.setState({currentImageIndex: this.state.currentImageIndex - 1})
            : this.setState({currentImageIndex: this.state.totalImages - 1});
        this.setState({activeSlideCSS: HomeStyles.slideRight});
        console.log("Previous Current: " + this.state.currentImageIndex)
        console.log("Previous Previous: " + this.state.previousImageIndex)
        this.shouldSlide = true;
        this.autoplay();
    }

    render() {
        let slideImage = this.state.images[this.state.currentImageIndex];
        let currentImage = this.state.images[this.state.previousImageIndex];

        return (
                <React.Fragment>
                    <button onClick={this.previousImage}>Previous</button>
                    <button onClick={this.nextImage}>Next</button>
                    <div className={HomeStyles.carouselContainer}>
                        {this.shouldSlide ? <img key={Math.random()} className={this.state.activeSlideCSS} src={slideImage.image}
                             alt={slideImage.alt} height="725px" width="100%"/> : null}
                        <img src={currentImage.image} alt={currentImage.alt} height="725px" width="100%"/>
                    </div>
                </React.Fragment>
        );
    }
}

