import React, {Component} from 'react';
import HomeStyles from "./HomePage.module.css"
import images from "./ImageGallery";

class HomePage extends Component {
    render() {
        return (
            <React.Fragment>
                <ImageGallery/>
                <ContactDivider/>
            </React.Fragment>
        );
    }
}

class ImageGallery extends Component {

    constructor(props) {
        super(props);
        this.state = {
            images: images,
            currentImage: 3,
            rotationTime:1000
        }
    }

    componentDidMount() {
        setInterval(this.switchImage, this.state.rotationTime);
    }

    switchImage = () => {
        this.setState({currentImage:this.state.currentImage++});
    }

    render() {
        let image = images[this.state.currentImage];

        return (
            <img src={image.image} alt={image.alt} height="725px"/>
        );
    }
}

const ContactDivider = () =>
    <div className={HomeStyles.headerContainer}>
        <div className={HomeStyles.header}>
            > Please contact us for detailed information for each service
        </div>
    </div>


export default HomePage;
