import React, {Component} from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
} from 'react-router-dom';

import * as NavigationLinks from "./NavigationLinks";
import "./NavigationBar.css"
import logo from "./images/logo_sharp.png"

class TopNavigationBar extends Component {
    setCurrentLinkColor (id) {
        document.getElementById(id).style.color = "#9155A7";
    }

    render() {
        return (
            <Router>
                <div>
                    <div className="navigationContainer">
                    <img src={logo} alt="Logo" height="200px" width="400px"/>
                    <nav>
                        <ul className="linksContainer">
                            {NavigationLinks.mainPageLinks.map(link => (
                                <li className="links" id={link.name}><a href={link.endpoint} onClick={this.setCurrentLinkColor(link.name)}>{link.name}</a></li>))}
                        </ul>
                    </nav>
                    </div>
                    <Switch>
                        {NavigationLinks.allLinks.map(link => (
                            <Route exact path={link.endpoint} component={link.page}/>))}
                    </Switch>
                </div>
            </Router>
        );
    }
}

class NavItem extends Component {
    changeColor = () => {
        this.setState({textColor:this.props.clickedTextColor})
    }

    constructor(props) {
        super(props);
        this.state = {
            linkObject: this.props.linkObject,
            textColor: this.props.textColor || "black",
            clickedTextColor: this.props.clickedTextColor
        }
    }

    render() {
       return (
             <li className="links" id={this.state.linkObject.name}>
                 <a href={this.state.linkObject.endpoint} onClick={this.changeColor()}>{this.state.linkObject.name}</a>
             </li>
       );
    }
}

export default TopNavigationBar;
